export enum ENTITY {
    USUARIO = 'USUARIO',
    ROL = 'ROL',
    CLIENTE = 'CLIENTE',
    DEMANDA = 'DEMANDA',
    PRODUCTO = 'PRODUCTO',
}



export type Module = {
    pkPermits: string;
    code: CODES;
    entity: ENTITY;
    active: boolean;
    description: string;
}

export type Permision = {
    active: boolean;
    modules: Module[]
}



export type ModulePermisionApp = Record<string, Permision>

